const isTop = (window.top === window.self);

if (isTop) {
    document
        .documentElement
        .classList
        .add("show-html-view");
}

function applyAnchorPoints() {
    function calcWithUnit(a, b, c, d) {
        function parseValue(str) {
            var i,
                ch;
            for (i = 0; i < str.length; i++) {
                ch = str.charAt(i);
                if ((ch < '0' || ch > '9') && ch !== '.' && ch !== '-') {
                    break;
                }
            }
            var num = parseFloat(str.substring(0, i));
            var unit = str.substring(i);
            return {num: num, unit: unit};
        }

        var pa = parseValue(a);
        var pb = parseValue(b);

        var resultA = (pa.num * c) + pa.unit;
        var resultB = (pb.num * d) + pb.unit;

        return {a: resultA, b: resultB};
    }

    // æŸ¥æ‰¾æ‰€æœ‰ class å« "anchorPoint_"
    var all = document.querySelectorAll('[class*="anchorPoint_"]');

    for (var i = 0; i < all.length; i++) {
        var el = all[i];
        var clsList = el.className.baseVal !== undefined
            ? el.className.baseVal
            : el.className;
        // æ‰¾åˆ° anchorPoint ç±»å‹
        var match = clsList.match(/anchorPoint_(\w+)/);
        if (!match) 
            continue;
        var type = match[1]; // center / lefttop / rightbottom ç­‰

        // è¯»å–å®½é«˜
        var width = 0,
            height = 0;
        if (el.getBBox) { // svg
            var bbox = el.getBBox();
            width = bbox.width + "px";
            height = bbox.height + "px";
        } else { // html
            width = el.offsetWidth + "px";
            height = el.offsetHeight + "px";
        }

        // æ ¹æ® type ç¡®å®š c d
        var c = 0,
            d = 0;

        switch (type) {
            case "center":
                c = -0.5;
                d = -0.5;
                break;
            case "lefttop":
            case "topleft":
                c = 0;
                d = 0;
                break;
            case "rightbottom":
            case "bottomright":
                c = 1;
                d = 1;
                break;
            case "topright":
            case "righttop":
                c = 1;
                d = 0;
                break;
            case "bottomleft":
            case "leftbottom":
                c = 0;
                d = 1;
                break;
            case "centertop":
            case "topcenter":
                c = -0.5; // æ°´å¹³å±…ä¸­
                d = 1; // é¡¶éƒ¨
                break;
            case "centerbottom":
            case "bottomcenter":
                c = -0.5; // æ°´å¹³å±…ä¸­
                d = 0; // åº•éƒ¨
                break;
            case "leftcenter":
            case "centerleft":
                c = 0; // å·¦ä¾§
                d = -0.5; // å‚ç›´å±…ä¸­
                break;
            case "rightcenter":
            case "centerright":
                c = 1; // å³ä¾§
                d = -0.5; // å‚ç›´å±…ä¸­
                break;
            default:
                c = 0;
                d = 0; // é»˜è®¤å€¼ï¼Œå¯é€‰
        }

        // å¯ä»¥æ‰©å±•æ›´å¤š anchorPoint è®¡ç®—åç§»
        var offset = calcWithUnit(width, height, c, d);
        var x = offset.a,
            y = offset.b;

        // å†™å…¥ transform
        if (el.style) {
            el.style.transform = "translate(" + x + "," + y + ")";
        } else {
            el.setAttribute("transform", "translate(" + x + "," + y + ")");
        }
    }
}

applyAnchorPoints();

function applyResizeScale() {
    svgRoot = document;
    const allElements = svgRoot.querySelectorAll('[class*="resizeTo_"]');

    allElements.forEach(el => {

        // ä» className æ‰¾åˆ° resizeTo_xx_yy
        const match = el
            .className
            .baseVal
            .match(/resizeTo_(\d+)_([0-9]+)/);
        if (!match) 
            return;

        const targetWidth = parseFloat(match[1]);
        const targetHeight = parseFloat(match[2]);

        let origW = null;
        let origH = null;

        let scaleX = 1;
        let scaleY = 1;
        let refEl = el; // é»˜è®¤ç›´æ¥ä½¿ç”¨å…ƒç´ æœ¬èº«

        // --------------------- å¦‚æœæ˜¯ <use> ---------------------
        if (el.tagName === 'use') {
            const href = el.getAttribute('href') || el.getAttribute('xlink:href');
            if (!href || !href.startsWith('#')) 
                return;

            const refId = href.slice(1);
            refEl = svgRoot.querySelector(`#${refId}`);
            if (!refEl) 
                return;
            }

        // --------------------- æ ¹æ®å¼•ç”¨å…ƒç´ ç±»å‹è·å–å°ºå¯¸ ---------------------
        if (refEl.tagName === 'rect') {
            origW = parseFloat(refEl.getAttribute('width'));
            origH = parseFloat(refEl.getAttribute('height'));

        } else if (refEl.tagName === 'circle') {
            const r = parseFloat(refEl.getAttribute('r'));
            origW = origH = 2 * r;

        } else if (refEl.tagName === 'ellipse') {
            const rx = parseFloat(refEl.getAttribute('rx'));
            const ry = parseFloat(refEl.getAttribute('ry'));
            origW = 2 * rx;
            origH = 2 * ry;

        } else if (refEl.tagName === 'image') {
            origW = parseFloat(refEl.getAttribute('width'));
            origH = parseFloat(refEl.getAttribute('height'));

        } else if (refEl.getBBox) {
            // fallbackï¼špath / g / symbol ç­‰
            const bbox = refEl.getBBox();
            origW = bbox.width;
            origH = bbox.height;
        }

        if (!origW || !origH) 
            return;

        // --------------------- è®¡ç®— scale ---------------------
        scaleX = targetWidth / origW;
        scaleY = targetHeight / origH;

        // --------------------- å†™å…¥ transform ---------------------
        const prev = el.getAttribute('transform') || '';
        const newTransform = `${prev} scale(${scaleX},${scaleY})`.trim();
        el.setAttribute('transform', newTransform);
    });
}

applyResizeScale();

function applyI18n(lang) {
    'i18n_welcome_en',
    'i18n_welcome_zh';
    const browserLang = lang || (navigator.language || navigator.userLanguage || 'en').toLowerCase();

    const currentLang = browserLang.split('-')[0];
    const elements = document.querySelectorAll('[class*="i18n_"]');

    const groups = new Map();

    elements.forEach(el => {
        const classList = Array.from(el.classList);
        const i18nClasses = classList.filter(cls => cls.startsWith('i18n_'));

        for (const cls of i18nClasses) {
            const parts = cls.split('_');
            if (parts.length !== 3 || parts[0] !== 'i18n') 
                continue;

            const name = parts[1];
            const lang = parts[2];

            if (!groups.has(name)) {
                groups.set(name, []);
            }

            groups
                .get(name)
                .push({element: el, lang});
        }
    });

    groups.forEach((items, name) => {
        if (items.length === 1) {
            items[0].element.style.display = '';
            return;
        }

        const matchCurrent = items.find(item => item.lang === currentLang);
        const matchEn = items.find(item => item.lang === 'en');

        const toShow = matchCurrent || matchEn || items[0];

        items.forEach(item => {
            item.element.style.display = 'none';
        });

        toShow.element.style.display = '';
    });
}
applyI18n();

